mapper.select:
insert into tbl_todo (title, dueDate, writer) values (
	#{title}, #{dueDate}, #{writer}
)

select tno, title, dueDate, writer, finished
  from tbl_todo
 order by tno desc;

mapper.update:
update tbl_todo
   set title = #{title}
     , dueDate = #{dueDate}
     , finished = #{finished}
 where tno = #{tno}
